import { useEffect, useState } from "react";
import { Button } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import TodoList from "../components/TodoList";
import { deleteTodos, getTodos } from "../utils/local";
import "../App.css";

function Home() {
  const navigate = useNavigate();

  const [search, setSearch] = useState("");
  const [todos, setTodos] = useState([]);

  const handleChangeSearch = (event) => {
    setSearch(event.target.value);
  };

  const onDeleteHandler = (index) => {
    deleteTodos(index);
    setTodos(getTodos());
  };

  const filteredTodos = todos.filter((todo) => {
    return todo.title.toLowerCase().includes(search.toLowerCase());
  });

  useEffect(() => {
    const data = getTodos();
    setTodos(data);
  }, []);

  return (
    <div className="p-3">
      <strong className="fs-1 text-light">Todo list rosanhusen</strong>
      <br />
      <Button
        className="col-12 mb-2 btn-outline-primary text-center"
        variant="light"
        type="submit"
        onClick={() => {
          navigate("/add");
        }}
      >
        Add Todo
      </Button>
      <input
        className="search-bar mb-2 col-12 px-2"
        type="text"
        onChange={(event) => {
          handleChangeSearch(event);
        }}
        value={search}
        placeholder="Cari Berdasarkan Title..."
      />
      <div>
        <TodoList todos={filteredTodos} onDelete={onDeleteHandler} />
      </div>
    </div>
  );
}

export default Home;
